    public OrdersCheckoutPage(AndroidDriver driver) {

        this.driver = driver;
    }
    public void accept_cookie() throws InterruptedException {
        Thread.sleep(10000);
        WebElement accept_cookie = driver.findElement(accept_cookie_button_id);
        accept_cookie.click();
        System.out.println("Çerezler kabul edildi.");
    }
    public void create_address(){
        WebElement createaddress = driver.findElement(create_address_button_xpath);
        createaddress.click();
        WebElement addressname1 = driver.findElement(address_name_xpath);
        addressname1.sendKeys("Adres");
        WebElement name = driver.findElement(name_xpath);
        name.sendKeys("Patika");
        WebElement surname = driver.findElement(surname_xpath);
        surname.sendKeys("Dev");
        WebElement phonenumber = driver.findElement(phone_number_xpath);
        phonenumber.sendKeys("05555555555");
        WebElement il_button = driver.findElement(il_button_xpath);
        il_button.click();
        WebElement istanbul =driver.findElement(istanbul_xpath);
        istanbul.click();
        WebElement ilce_button = driver.findElement(ilce_button_xpath);
        ilce_button.click();
        WebElement besiktas = driver.findElement(besiktas_xpath);
        besiktas.click();
        WebElement mahelle = driver.findElement(mahalle_button_xpath);
        mahelle.click();
        WebElement etiler = driver.findElement(etiler_xpath);
        etiler.click();
        WebElement address = driver.findElement(address_xpath);
        address.sendKeys("Adres adres adres adres adres adres");
        address.click();
        WebElement save = driver.findElement(save_button_xpath);
        save.click();
        System.out.println("Adres bilgileri girildi.");
        WebElement saveandcontinue = driver.findElement(save_and_continue_xpath);
        saveandcontinue.click();
    }
    public void check_on_the_payment_screen(){
        WebElement pay = driver.findElement(pay_xpath);
        String str_pay = pay.getAttribute("text");
        Assertions.assertEquals("Kart ile ödeme",str_pay, "Hata! Ödeme ekranına gelindiği doğrulanamdı.");
        System.out.println("Ödeme ekranına gelindiği doğrulandı.");
    }
